changeSet.sql:
DROP TABLE IF EXISTS public.immunization;
CREATE SEQUENCE immunization_id_seq;
CREATE TABLE public.immunization
(
    id bigint NOT NULL DEFAULT nextval('immunization_id_seq'),
    patient_id bigint,
    patient_uuid character varying(100),
    immunization_type character varying,
    unique_immunization_data character varying,
    vaccination_date date,
    archived integer,
    PRIMARY KEY (id)
);
ALTER SEQUENCE immunization_id_seq OWNED BY immunization.id;
